mapper.select:
SELECT km_doc.*,kdbt.business_types,kdv.versions,kmtopic.topic_ids AS topic_ids,
!isnull(kdf.id ) as favourite
DECODE(kdf.id,null,0,1 ) as favourite
FROM km_doc LEFT JOIN (SELECT doc_id,
CONVERT( group_concat(business_type) USING utf8)
wm_concat(to_char(business_type))
AS business_types FROM KM_DOC_BUSINESS_TYPE GROUP BY doc_id) kdbt ON kdbt.doc_id=km_doc.id LEFT JOIN (SELECT DOC_ID,
CONVERT( group_concat(version) USING utf8)
wm_concat(to_char(version))
AS versions FROM KM_DOC_VERSION GROUP BY DOC_ID) kdv ON kdv.doc_id=km_doc.id LEFT JOIN (SELECT doc_id,
CONVERT( group_concat(sys_category.id) USING utf8)
wm_concat(to_char(sys_category.id))
AS topic_ids FROM sys_category JOIN km_doc_topic_type kdtt ON sys_category.id=kdtt.topic_id GROUP BY doc_id) kmtopic ON kmtopic.doc_id=km_doc.id left JOIN km_doc_favourite kdf on km_doc.id=kdf.doc_id and kdf.user_id='${userId}'
release_flag = #{kmDocParamVO.releaseFlag}
and public_flag = #{kmDocParamVO.publicFlag}
and create_by = #{kmDocParamVO.createBy}
and serial_number = #{kmDocParamVO.serialNumber}
and file_no like
concat('%', #{kmDocParamVO.fileNo} , '%')
concat(concat('%', #{kmDocParamVO.fileNo} ),'%')
and category = #{kmDocParamVO.category}
and title like
concat('%', #{kmDocParamVO.title} , '%')
concat(concat('%', #{kmDocParamVO.title} ),'%')
and source in
#{source}
and keywords like
concat('%',#{kmDocParamVO.keywords},'%')
concat(concat('%',#{kmDocParamVO.keywords}),'%')
and file_type = #{kmDocParamVO.fileType}
and pub_time <= #{kmDocParamVO.pubTimeEnd}
and pub_time >= #{kmDocParamVO.pubTimeStart}
and create_time <= #{kmDocParamVO.createTimeEnd}
and create_time >= #{kmDocParamVO.createTimeStart}
and km_doc.status in
#{status}
and EXISTS( SELECT 0 FROM km_doc_business_type kbt WHERE kbt.doc_id=km_doc.id AND kbt.business_type IN
#{businessType}
)
AND EXISTS( SELECT 0 FROM km_doc_version kdv WHERE kdv.doc_id=km_doc.id AND kdv.version IN
#{version}
)
${permissionSql}
${orderBy}
order by km_doc.last_update_time desc, km_doc.create_time desc
